import pic from '../profilePic.png'
import NavBar from './navigationBar.js';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons'

function getHome() {
    return (
        <div style={HomeCSS}>
            <NavBar></NavBar>
            <div style={bodyCSS} id="Body">
                {getImg()}
                <div style={{margin: '20px', textAlign: "center"}}>
                    <h1 style={{fontSize: "60px"}}>
                        Hi, I'm Eric!
                    </h1>
                    <h3 style={paragraphCSS}>
                        I'm a software architect who recently graduated with a B.S. in computer science from the University of California San Diego.
                    </h3>
                </div>
            </div>
            {getSocial()}
        </div>
    )
}

function getImg() {
    return (
    <img src={pic} className="Profile-pic" alt="profile pic" 
                        width="200px" 
                        height="200px" 
                        style={{margin: "50px", float: "left"}}></img>
    )
}

function getSocial() {
    return(
        <ul listStyle="none outside" style={socialCSS}>
            <li style={socialItemBoxCSS}>
                <a href="https://www.linkedin.com/in/eric-wei-09b36b141/" style={socialLinkCSS}>
                    <FontAwesomeIcon icon={faCoffee} />
                </a>
            </li>
        </ul>
    )
}

const socialCSS = {
    margin: "24px 0",
    padding: "0",
    fontSize: "30px",
    textShadow: "0px 1px 2px rgba(0, 0, 0, .8)",
    display: "block"
}

const socialItemBoxCSS = {
    display: "inline-block",
    margin: "0 15px",
    padding: "0",
    lineHeight: "18px"
}

const socialLinkCSS = {
    color: "#fff",
    textDecoration: "none",
    outline: "0"
}

const iconCSS = {
    display: "inline-block",
    fontFamily: "FontAwesome",
    fontStyle: "normal",
    fontWeight: "normal",
    lineHeight: "1"
}

const HomeCSS = {
    backgroundColor: "#282c34",
    minHeight: "100vh",
    fontSize: `calc(10px + 2vmin)`
}
const bodyCSS = {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    color: "white",
    margin: "175px 75px"
}

const paragraphCSS = {
    color: "#A8A8A8", 
    textShadow: "0px 1px 2px rgba(0, 0, 0, .5)",
    font: "16px/1.9em 'LibreBaskerville Regular', serif",
    maxWidth: "410px"
}
export default getHome;
