import React, { useState } from 'react';

function NavBar() {
    const items = [{name: "Home", id: "Body"}, 
                    {name: "Education", id: "Education"}, 
                    {name: "Experience", id: "Experience"}, 
                    {name: "Projects", id: "Projects"}];

    const states = [];
    states.push(React.useState(false));
    states.push(React.useState(false));
    states.push(React.useState(false));
    states.push(React.useState(false));

    return (
        <div>
            <ul id = "nav" style={navCSS}>
                {items.map((item, index) => {
                    console.log(index);
                    var color = {color : states[index][0] === false ? "#0b4fba" : "white"};
                    return (
                    <li key={item}>
                        <a onMouseEnter={() => states[index][1](true)} 
                            onMouseLeave={() => states[index][1](false)} 
                            href={'#'+item.id} style={navItemCSS + color}>{item.name}</a>
                    </li>)
                })}
            </ul>
        </div>
    )
}

var navCSS = {
    width: "100%",
    margin: "0 0 0 0",
    display: "flex",
    flexDirection: "row",
    listStyle: "none",
    backgroundColor: "white"
}

const navItemCSS = {
    display: "block",
    padding: "4px 8px",
    margin: "10px",
    fontSize: "20px",
    textDecoration: "none"
}

export default NavBar;
