import React, { useState } from 'react';

function NavBar() {
    const items = [{name: "Home", id: "Body"}, 
                    {name: "Education", id: "Education"}, 
                    {name: "Experience", id: "Experience"}, 
                    {name: "Projects", id: "Projects"}];

    function changeToHoverColor(e) {
        e.target.style.color = "#c00";
    }
    function changeToNonHoverColor(e) {
        e.target.style.color = "#0b4fba";
    }
    return (
        <div>
            <ul id = "nav" style={navCSS}>
                {items.map((item, index) => {
                    return (
                    <li key={item, index}>
                        <a onMouseEnter={changeToHoverColor} 
                            onMouseLeave={changeToNonHoverColor} 
                            href={'#'+item.id} style={navItemCSS}>{item.name}</a>
                    </li>)
                })}
            </ul>
        </div>
    )
}

var navCSS = {
    width: "auto",
    margin: "0 0 0 0",
    display: "flex",
    flexDirection: "row",
    listStyle: "none",
    backgroundColor: "white",
    borderBottom: "1px solid #ccc",
    minHeight: "48px"
}

const navItemCSS = {
    display: "block",
    padding: "4px 8px",
    margin: "10px",
    fontSize: "20px",
    textDecoration: "none",
    color: "#0b4fba"
}

export default NavBar;
