import pic from '../profilePic.jpg'
function getBody() {
    return (
        <div style={bodyCSS} id="Body">
          {getImg()}
          <div style={{margin: '20px'}}>
            <h1 style={{fontSize: "60px"}}>
                Hi, my name is Eric Wei.
            </h1>
            <h3 style={{fontSize: "16px/1.9em", color: "#A8A8A8", margin: "0 auto", width: "70%"}}>
                I'm a software architect.
            </h3>
        </div>
      </div>
    )
}

function getImg() {
    return (
    <img src={pic} className="Profile-pic" alt="profile pic" 
                        width="270px" 
                        height="360px" 
                        style={{margin: "50px"}}></img>
    )
}

const bodyCSS = {
    backgroundColor: "#282c34",
    minHeight: "100vh",
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    fontSize: `calc(10px + 2vmin)`,
    color: "white"
}

export default getBody;
